import React, { Component } from 'react';
import {
  View,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Text,
  WebView
} from 'react-native';

import { Header, Icon } from 'react-native-elements';

import DropDownItem from 'react-native-drop-down-item';

const IC_ARR_DOWN = require('./subscreens/icons/ic_arr_down.png');
const IC_ARR_UP = require('./subscreens/icons/ic_arr_up.png');

export default AboutScreen = () => {

  return (
    <View style={styles.container}>
      <Header
        barStyle="light-content"
        leftComponent={
          <TouchableOpacity onPress={() => this.props.navigation.navigate('Home')}>
            <View style={{ flexDirection: 'row', alignItems: 'center' }}>
              <Icon
                name='chevron-left'
                color='#007AFF'
                size={28}
              />
              <Text style={{ color: '#007AFF' }}>
                Home
            </Text>
            </View>
          </TouchableOpacity>
        }
        rightComponent={{ icon: 'share', color: '#007AFF', size: 18, marginRight: 15 }}
        containerStyle={{
          backgroundColor: '#eee',
          justifyContent: 'space-around',
        }}
      />
      <View style={styles.contentContainer}>
        <WebView
          source={{ uri: 'https://sun.edu.ng/about-sun/' }}
          style={{ paddingTop: 0 }}
        />
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  contentContainer: {
    flex: 1,
    paddingLeft: 15,
    paddingRight: 15,
    paddingTop: 5
  },
  titleBar: {
    backgroundColor: '#eee',
    paddingTop: 0,
    paddingLeft: 18,
    paddingBottom: 5
  }
});
