import React, { Component } from 'react';
import {
  View,
  ScrollView,
  WebView,
  StyleSheet,
  Text,
  TouchableOpacity,
  Image
} from 'react-native';

import { Header, Icon } from 'react-native-elements';

import {
  Container,
  Tab,
  Tabs
} from 'native-base';

import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp
} from 'react-native-responsive-screen';

import Accordion from 'react-native-collapsible/Accordion';

import Today from './subscreens/Today';
import Requirements from './subscreens/Requirements';
import Application from './subscreens/Application';

export default NewsScreen = () => {
  return (
    <Container>
      <Header
        barStyle="light-content"
        leftComponent={
          <TouchableOpacity onPress={() => this.props.navigation.navigate('Home')}>
            <View style={{ flexDirection: 'row', alignItems: 'center' }}>
              <Icon
                name='chevron-left'
                color='#007AFF'
                size={28}
              />
              <Text style={{ color: '#007AFF' }}>
                Home
                </Text>
            </View>
          </TouchableOpacity>
        }
        rightComponent={{ icon: 'share', color: '#007AFF', size: 18, marginRight: 15 }}
        containerStyle={{
          backgroundColor: '#eee',
          justifyContent: 'space-around',
        }}
      />
      <WebView
        source={{ uri: 'https://sun.edu.ng/news/' }}
        style={{ paddingTop: 0 }}
      />

    </Container>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    padding: 15
  },
  titleBar: {
    backgroundColor: '#eee',
    paddingTop: 0,
    paddingLeft: 18,
    paddingBottom: 5
  }

});
