import React, { Component } from 'react';
import {
  View,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  Image
} from 'react-native';


import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp
} from 'react-native-responsive-screen';

import { LinearGradient } from 'expo-linear-gradient';

export default HomeScreen = ({ navigation }) => {
  return (
    <View>
      <ScrollView>
        <View style={styles.container}>
          <Image
            source={require('../../assets/images/logo_col.png')}
            style={styles.logo}
            resizeMode='contain'
          />

          <View style={styles.menuContainer}>
            <TouchableOpacity onPress={() => navigation.navigate('Admission')} style={styles.menuOption}>
              <LinearGradient
                colors={['#01dd9b', '#14bb9d']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/admission.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  Admission
                </Text>
              </LinearGradient>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => navigation.navigate('Programs')}>
              <LinearGradient
                colors={['#fc6d93', '#d3359e']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/programs.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  Programs
              </Text>
              </LinearGradient>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate('Soon')}>
              <LinearGradient
                colors={['#8057fc', '#5d4fed']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/test.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  Aptitude Test
              </Text>
              </LinearGradient>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate('News')}>
              <LinearGradient
                colors={['#fb831c', '#f1264f']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/news.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  News & Events
              </Text>
              </LinearGradient>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate('Map')}>
              <LinearGradient
                colors={['#00c4c6', '#069ec3']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/map.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  Maps & Directions
              </Text>
              </LinearGradient>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate('Soon')}>
              <LinearGradient
                colors={['#25446c', '#003067']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/tour.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  Virtual Tour
              </Text>
              </LinearGradient>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate('About')}>
              <LinearGradient
                colors={['#fe4843', '#db0a6c']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/about.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  About Us
              </Text>
              </LinearGradient>
            </TouchableOpacity>

            <TouchableOpacity onPress={() => navigation.navigate('Login')}>
              <LinearGradient
                colors={['#2874e2', '#5b49e4']}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.menu}>
                <Image
                  source={require('../../assets/images/menu/login.png')}
                  style={styles.menuIcon}
                  resizeMode='contain'
                />
                <Text style={[styles.white, styles.right]}>
                  Login
              </Text>
              </LinearGradient>
            </TouchableOpacity>

          </View>

        </View>
      </ScrollView>
    </View>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    padding: 10
  },

  menuContainer: {
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    alignItems: 'baseline',
    flexWrap: 'wrap',
    width: wp('95%'),
  },

  logo: {
    width: wp('60%'),
    height: hp('20%'),
    marginBottom: -10
  },

  menu: {
    flexShrink: 1,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'stretch',
    width: wp('40%'),
    height: hp('18%'),
    borderRadius: 5,
    marginBottom: 18,
    padding: 15
  },

  menuIcon: {
    flexShrink: 1,
    maxHeight: 80,
    width: 80,
  },

  right: {
    alignSelf: 'flex-end'
  },

  white: {
    color: '#fff'
  },
});
