import React, { Component } from 'react'
import {
  View,
  StyleSheet,
  Text,
  Image,
  TouchableOpacity
} from 'react-native';

import {
  Container,
  Tab,
  Tabs
} from 'native-base';

import {
  Header,
  Icon
} from 'react-native-elements'

export default ProfileScreen = ({navigation}) => {
    return (
      <Container>
        <Header
          barStyle="light-content"
          leftComponent={
            <TouchableOpacity onPress={() => navigation.navigate('Home')}>
              <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                <Icon
                  name='chevron-left'
                  color='#007AFF'
                  size={28}
                />
                <Text style={{ color: '#007AFF' }}>
                  Home
                </Text>
              </View>
            </TouchableOpacity>
          }
          rightComponent={
            <TouchableOpacity onPress={() => navigation.navigate('Home')}>
              <View style={{ flexDirection: 'row', alignItems: 'center' }}>
                <Icon
                  name=''
                  color='#120b2d'
                  size={28}
                />
                <Text style={{ color: '#120b2d' }}>
                  Logout
                </Text>
              </View>
            </TouchableOpacity>
          }
          containerStyle={{
            backgroundColor: '#eee',
            justifyContent: 'space-around',
          }}
        />
        <View style={styles.titleBar}>
          <Text style={{ fontSize: 22, fontWeight: 'bold' }}>
            Profile
          </Text>
        </View>
        <View style={styles.profileContainer}>
          <View style={styles.thumbnailContainer}>
            <Image source={require('../../../assets/images/person.jpg')} style={styles.thumbnail} />
          </View>
          <View style={styles.detailsContainer}>
            <Text> Abdulganiy Abdulkareem </Text>
            <Text> Software Engineering </Text>
            <Text> 200 L </Text>
            <Text> 2.45 CGPA</Text>
          </View>
        </View>

        <Tabs tabBarUnderlineStyle={{ backgroundColor: '#007AFF' }}>
          <Tab heading="CURRENT SEMESTER"
            tabStyle={{ backgroundColor: '#fff' }}
            textStyle={{ color: 'black', textAlign: 'center' }}
            activeTabStyle={{ backgroundColor: '#fff' }}
            activeTextStyle={{ color: 'black', fontWeight: 'normal', textAlign: 'center' }}>

          </Tab>

          <Tab heading="MFEES"
            tabStyle={{ backgroundColor: '#fff' }}
            textStyle={{ color: 'black', textAlign: 'center' }}
            activeTabStyle={{ backgroundColor: '#fff' }}
            activeTextStyle={{ color: 'black', fontWeight: 'normal', textAlign: 'center' }}>
           
          </Tab>

          <Tab heading="GRADUATION PLAN"
            tabStyle={{ backgroundColor: '#fff' }}
            textStyle={{ color: 'black', textAlign: 'center' }}
            activeTabStyle={{ backgroundColor: '#fff' }}
            activeTextStyle={{ color: 'black', fontWeight: 'normal', textAlign: 'center' }}>
            
          </Tab>
        </Tabs>
      </Container>
    )
}

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  titleBar: {
    backgroundColor: '#eee',
    paddingTop: 0,
    paddingLeft: 18,
    paddingBottom: 5
  },
  profileContainer: {
    flex: 1,
    flexDirection: 'row',
    padding: 15
  },
  thumbnailContainer: {
    flex: 1
  },
  detailsContainer: {
    flex: 2
  },
  thumbnail: {
    width: 100,
    height: 100,
    resizeMode: 'contain',
    borderRadius: 90
  }
})
