import React, { Component } from 'react';
import {
  View,
  StyleSheet,
  TouchableOpacity,
  KeyboardAvoidingView,
  Text,
  Image,
  TextInput
} from 'react-native';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp
} from 'react-native-responsive-screen';
import { Button } from 'react-native-elements';

export default LoginScreen = ({navigation}) => {
  return (
    <View style={styles.container}>
      <View style={{ marginTop: 10 }}>
        <Image
          source={require('../../assets/images/logo.png')}
          style={styles.logo}
          resizeMode='contain'
        />
      </View>

      <View style={{ flexDirection: 'column' }}>
        <Button title="Apply Now" buttonStyle={styles.applyButton}
          onPress={() => navigation.navigate('Admission')} />

        <View style={{ flexDirection: 'row', marginTop: 10 }}>
          <View style={[styles.inputContainer, styles.refNoInput]}>
            <TextInput
              style={{ height: 40, paddingLeft: 10 }}
              placeholder="Application ref number"
              onChangeText={(text) => this.setState({ text })}
            />
          </View>
          <Button title="Check Status" buttonStyle={styles.statusButton}
            onPress={() => navigation.navigate('CheckStatus')} />
        </View>
      </View>
      <View>
        <Text style={{ textAlign: 'center' }}>
          Are you a Visitor?
            </Text>
        <TouchableOpacity onPress={() => navigation.navigate('Home')}>
          <Text style={{ color: '#d55154', textAlign: 'center' }}>
            Click here!
              </Text>
        </TouchableOpacity>
      </View>
      <KeyboardAvoidingView behavior="padding" style={{ marginBottom: 20 }}>
        <View style={[styles.inputContainer, styles.loginInput]}>
          <TextInput
            style={{ height: 40, paddingLeft: 10 }}
            placeholder="Username"
            onChangeText={(text) => this.setState({ text })}
            returnKeyType={"next"}
          />
        </View>
        <View style={[styles.inputContainer, styles.loginInput, { marginTop: 10 }]}>
          <TextInput
            style={{ height: 40, paddingLeft: 10 }}
            placeholder="Password"
            onChangeText={(text) => this.setState({ text })}
            returnKeyType={"done"}
            textContentType={"password"}
          />
        </View>
        <Button title="Login" buttonStyle={styles.loginButton} 
        onPress={() => navigation.navigate('Loginned')} />
      </KeyboardAvoidingView>
    </View>
  );
}
;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-around',
    alignItems: 'center'
  },
  logo: {
    width: wp('35%'),
    height: hp('20%')
  },
  applyButton: {
    backgroundColor: '#d55154',
    width: 300
  },
  inputContainer: {
    justifyContent: 'center',
    borderRadius: 4,
    borderWidth: 0.5,
    borderColor: '#d6d7da',
    marginRight: 5
  },
  refNoInput: {
    width: 183,
  },
  loginInput: {
    width: 300
  },
  statusButton: {
    backgroundColor: 'black'
  },
  loginButton: {
    backgroundColor: '#FF9500',
    width: 300,
    marginTop: 15,
    marginBottom: 15
  },
});
